import React from "react";
import { useState } from "react";
import styled from "styled-components";
import UserRegister from "../../modal";
import createUser from "./RegisterApi";

interface Props {
  UserRegister: UserRegister[];
  setRegisterUser: React.Dispatch<React.SetStateAction<UserRegister[]>>;
}

const Register: React.FC<Props> = ({
  UserRegister,
  setRegisterUser,
}: Props) => {
  const [UsernameReg, setUsernameReg] = useState<string>("");
  const [passwordReg, setpasswordReg] = useState<string>("");

  const HandleRegister = (e: React.FormEvent) => {
    e.preventDefault();

    if (UsernameReg && passwordReg) {
      setRegisterUser([
        ...UserRegister,
        { username: UsernameReg, password: passwordReg },
      ]);
      setUsernameReg("");
      setpasswordReg("");
      createUser(UsernameReg, passwordReg);
    } else {
      if (!UsernameReg) {
        console.log("Enter Username");
      }
      if (!passwordReg) {
        console.log("Enter Password");
      }
    }
  };

  return (
    <SignUp>
      <Form onSubmit={HandleRegister}>
        <span>Sign Up</span>

        <label htmlFor="Email">
          Email
          <input
            type="email"
            value={UsernameReg}
            onChange={(e) => setUsernameReg(e.target.value)}
          />
        </label>

        <label htmlFor="First Name">
          First Name
          <input type="text" />
        </label>

        <label htmlFor="Last Name">
          Last Name
          <input type="text" />
        </label>

        <label htmlFor="password">
          Password
          <input
            type="password"
            value={passwordReg}
            placeholder="Enter Password"
            onChange={(e) => setpasswordReg(e.target.value)}
          />
        </label>

        <button type="submit">Register</button>
      </Form>
      <ImageWrapper>
        <img
          src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-registration/draw1.webp"
          alt="signup"
        />
      </ImageWrapper>
    </SignUp>
  );
};

export default Register;

const SignUp = styled.div`

  background-color: white;
  padding: 2rem;
  display: flex;
  flex-direction: row;
  height: 28rem;
  width: 60rem;
  border-radius: 1.5rem;
  align-items: center;
  justify-content: space-around;
  box-shadow: 0 2px 15px -3px rgba(0, 0, 0, 0.07),
    0 10px 20px -2px rgba(0, 0, 0, 0.04);
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 1rem;

  span {
    font-family: Roboto, sans-serif;
    font-size: 2.5rem;
    font-weight: 900;
    margin-bottom: 1.5rem;
  }

  label {
    display: flex;
    flex-direction: column;
    font-size: 13px;
    color: black;
    position: relative;
    font-weight: bold;
    line-height: 16px;
    letter-spacing: 0.02em;
    font-family: "Roboto",sans-serif;
    gap: 0.5rem;
    margin-bottom: 1rem;
    font-size:1rem;

    input {

      width: 18.75rem;
      height: 2.25rem;
      font-size: 14px;
      line-height: 20px;
      border-radius:0.375rem;
      border:0.1rem solid black;

    }
  }

  button {
    height: 32px;
    width: 90px;
    font-size: 15px;
  }
`;

const ImageWrapper = styled.div`
  img {
    height: 300px;
    width: 500px;
  }
`;
