import React from "react";
import { useState } from "react";
import UserRegister from "../../modal";

interface Props {
  UserRegister: UserRegister[];
  setUser: React.Dispatch<React.SetStateAction<UserRegister[]>>;
}

const Register: React.FC<Props> = ({ UserRegister, setUser }: Props) => {
  const [UsernameReg, setUsernameReg] = useState<string>("");
  const [passwordReg, setpasswordReg] = useState<string>("");

  const HandleRegister = (e: React.FormEvent) => {
    e.preventDefault();

    if (UsernameReg && passwordReg) {
      setUser([
        ...User,
        { id: Date.now(), username: UsernameReg, password: passwordReg },
      ]);
      setUsernameReg('');
      setpasswordReg('');
    } else {
      if (!UsernameReg) {
        console.log("Enter Username");
      }
      if (!passwordReg) {
        console.log("Enter Password");
      }
    }
  };
  // console.table(userData);

  return (
    <div>
      <form onSubmit={HandleRegister}>
        <label htmlFor="name">Username</label>
        <input
          type="text"
          value={UsernameReg}
          placeholder="Enter Username"
          onChange={(e) => setUsernameReg(e.target.value)}
        />
        <label htmlFor="password">Password</label>
        <input
          type="password"
          value={passwordReg}
          placeholder="Enter Password"
          onChange={(e) => setpasswordReg(e.target.value)}
        />
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default Register;
